package begin_with_java_loop_practice;

public class WAP_to_print_sum_of_first_ten_nautral_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            
           int sum = 0;
            for(int i=1;i<=10;i++)
            	sum = sum+i; 
            System.out.println(" Sum of first ten natural numbers :"  + sum);
	}

}
